        return new Source("{% extends 'base.html.twig' %}
{% form_theme form 'bootstrap_4_layout.html.twig' %}

{% block title %}
    Big Bill | Modifier le role
{% endblock %}

{% block body %}
    <h1>Modifier le rôle de l'utilisateur</h1>

    <section class=\"sheet\">
        {{ form_start(form) }}
        <div class=\"container\">
            <div class=\"row\">
                <div class=\"col-12\">
                    <div class=\"card\">
                        <div class=\"card-body\">
                            <div class=\"form-group\">
                                <div class=\"col-md-10\">
                                    {{ form_label(form.roles, \"Rôles disponibles\") }}
                                </div>
                                <div class=\"col-md-2\">
                                    {{ form_widget(form.roles) }}
                                </div>
                            </div>
                        </div>
                        <div class=\"card-footer\">
                            <button class=\"btn btn-primary float-right\">
                                <i class=\"fas fa-check m-1\"></i>Modifier le rôle</button>
                        </div>
                    </div>
                </div>
            </div>
            <div class=\"row mt-4 float-right\">
                <div class=\"col-12\">
                    <a href=\"{{ path('user_index') }}\" class=\"btn btn-outline-primary form-btn\">
                        <i class=\"fa fa-arrow-left m-1\"></i>Revenir sur la page précédente</a>
                </div>
            </div>
        </div>
        {{ form_end(form) }}
    </section>
{% endblock %}
