    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Big Bill | Catégorie{% endblock %}

{% block body %}
    <h1>Liste des catégories</h1>

    <div class=\"text-right\">
        <a class=\" btn btn-primary mb-3\" href=\"{{ path('category_new') }}\"><i class=\"fas fa-plus-circle mr-1\"></i>Ajouter une catégorie</a>
    </div>
    <table class=\"table table-hover\">
        <thead>
        <tr>
            <th class=\"text-center\">Nom</th>
            <th class=\"text-center\">Actions</th>
        </tr>
        </thead>
        <tbody>
        {% for category in categories %}
            <tr>
                <th class=\"text-center\">{{ category.name }}</th>
                <td class=\"btn-row\">
                    {% if is_granted('ROLE_ADMIN') %}
                        {{ include('category/_delete_form.html.twig') }}
                    {% endif %}
                    <a title=\"Editer\" class=\"btn btn-sm btn-primary ml-2\"
                       href=\"{{ path('category_edit', {'id': category.id}) }}\">
                        <i class=\"fas fa-pencil-alt\"></i>
                    </a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"2\">Pas de catégorie enregistrée</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}

{% block stylesheets %}
    {{ encore_entry_link_tags('app') }}
    {{ encore_entry_link_tags('category') }}
{% endblock %}
", "category/index.html.twig", "/var/www/html/templates/category/index.html.twig");
    }
